mapper.select:
SELECT * FROM t_user
WHERE email = #{email}

    AND provider = #{provider}

mapper.update:
UPDATE t_user


        mainProfile = #{mainProfile}


        pw = #{pw}


        nm = #{nm}


WHERE iuser = #{iuser}

UPDATE t_user A INNER JOIN t_user_profile B ON A.iuser = B.iuser AND B.iprofile = #{iprofile} SET A.mainProfile = B.img WHERE A.iuser = #{iuser}
INSERT INTO t_user_follow (from_iuser, to_iuser) VALUES (${from_iuser}, ${to_iuser})
SELECT B.* FROM t_user_follow A INNER JOIN t_user B ON A.from_iuser = B.iuser WHERE A.to_iuser = ${iuser}
SELECT B.* FROM t_user_follow A INNER JOIN t_user B ON A.to_iuser = B.iuser WHERE A.from_iuser = ${iuser}
DELETE FROM t_user_follow WHERE from_iuser = ${from_iuser} AND to_iuser = ${to_iuser}
INSERT INTO t_movie_fav (iuser, title, image) VALUES (${iuser}, #{title}, #{image})
SELECT * FROM t_movie_fav WHERE iuser = ${iuser}
DELETE FROM t_movie_fav WHERE iuser = ${iuser} AND title = #{title}
SELECT mainProfile FROM t_user WHERE iuser = #{iuser}
